import React, { useEffect, useState } from 'react'
import { Card, Col } from 'react-bootstrap'
import { useParams } from 'react-router-dom';
import NavBar from '../components/NavBar';
import Footer from '../components/Footer';
import CartButtons from '../components/CartButtons';
import { doc, getDoc, getFirestore } from "firebase/firestore";

const ProductDetail = () => {
    const [loading, setLoading] = useState(true);
    const [productData, setProductData] = useState({});
    const [error, setError] = useState(false);

    const {productId} = useParams();
    useEffect(() => {
        const db = getFirestore();
        const productosArr = doc(db, "products", productId);
        getDoc(productosArr)
        .then((snapshot)=>{
            setProductData([{id: snapshot.id, ...snapshot.data()}]);
        })
        .catch((error)=> setError(true))
        .then(()=>setLoading(false));
    }, [productId]);
    console.log(productData);
    return loading ? (<div>Loading ...</div>) : (
        <div>
            <NavBar />
            <Col key={productData[productId]}>
            <Card style={{ width: '18rem' }}>
            <Card.Img variant="top" src={productData[productId].imagen} />
            <Card.Body>
                <Card.Title>{productData[productId].nombre}</Card.Title>
                <Card.Text>
                {productData[productId].precio}
                </Card.Text>
                <CartButtons />
            </Card.Body>
            </Card>
            </Col>
            <Footer />
        </div>
    );
}

export default ProductDetail
