import React, { useEffect, useState } from 'react'
import productosArr from '../api/productos.json'
import { useParams } from 'react-router-dom';
import Item from '../components/Item/Item';

const CategoriesDetail = () => {
    const [loading, setLoading] = useState(true);
    const [productData, setProductData] = useState({});
    
    const {categorie} = useParams();

    useEffect(() => {
        setProductData(productosArr);
        setLoading(false);
    }, []);

    let productDataFlt = productData.filter(e=>e.nombre.toLowerCase().includes(categorie.toLowerCase()))
    console.log(productDataFlt);
    return loading ? (<div>Loading ...</div>) : (
        <Item productData={productDataFlt}/>
    );
}

export default CategoriesDetail
