import React, { useContext } from "react";

import { Button } from "react-bootstrap";
import { CartContext } from "../context/CartContext";


const CartButtons = () => {
    const [state, setState] = useState(0);
    const [count, setCount] = useContext(CartContext);

    const handleAddClick = () => {
        setState(state + 1);
        setCount(count + 1);
    }
    const handleLessClick = () => {
        setState(state - 1);
        setCount(count - 1);
    }
    return (
        <div className="d-flex align-items-center">
            <div className="d-flex w-25">
                <Button variant="outline-secondary" className="rounded-0" onClick={handleLessClick}>
                    -
                </Button>
                <input
                    type="text"
                    className="form-control form-control-sm text-center rounded-0"
                    placeholder="Cantidad custom"
                    id="valueInput"
                />
                <Button variant="outline-secondary" className="rounded-0" onClick={handleAddClick}>
                    +
                </Button>
            </div>
            <Button className="ml-2" variant="primary">
                Agregar al Carrito
            </Button>
        </div>
    );
};



export default CartButtons;
