import React, { useContext, useState } from "react";

import { Button } from "react-bootstrap";
import { CartContext } from "../context/CartContext";


const CartButtons = (productId) => {
    const [state, setState] = useState(0);    
    const {count, setCount} = useContext(CartContext);

const handleAddClick = () => {
    setCount(count + 1);
    setState(state + 1);
};
const handleLessClick = () => {
    setCount(count - 1);
    setState(state - 1);
};

    const addToCart = () =>{
        if(count.products.length<1) return;
        const existe = count.products.find(
            (p) => p.id === productId
        );
        if(existe) {
            existe.cantidad += state
        } else {
            const newProduct = {
                productId,
                cantidad: state,
            };
            setCount((prevState) => ({
                cantidad: prevState.cantidad + state,
                products:[...prevState.products ,newProduct ],
            }));
        }
    };
    return (
        <div className="d-flex align-items-center">
            <div className="d-flex w-25">
                <Button variant="outline-secondary" className="rounded-0" onClick={handleLessClick}>
                    -
                </Button>
                <span>{state}</span>
                <Button variant="outline-secondary" className="rounded-0" onClick={handleAddClick}>
                    +
                </Button>
            </div>
            <Button className="ml-2" variant="primary" onClick={addToCart}>
                Agregar al Carrito
            </Button>
        </div>
    );
};



export default CartButtons;
