import React, { useContext, useState } from "react";

import { Button } from "react-bootstrap";
import { CartContext } from "../context/CartContext";


const CartButtons = (productId) => {
    const [state, setState] = useState(0);    
    const [count, setCount] = useContext(CartContext);
    const addToCart = (e) =>{
        const existe = count.products.find(
            (p) => p.productId === productId
        );
        if(existe) {
            existe.cantidad += state
        } else {
            const newProduct = {
                productId,
                cantidad: state,
            };
            setCount((prevState) => ({
                cantidad: prevState.cantidad + state,
                products:[...prevState.products ,newProduct ],
            }));
        }
    };
    return (
        <div className="d-flex align-items-center">
            <div className="d-flex w-25">
                <Button variant="outline-secondary" className="rounded-0" onClick={addToCart}>
                    -
                </Button>
                <input
                    type="text"
                    className="form-control form-control-sm text-center rounded-0"
                    placeholder="Cantidad custom"
                    id="valueInput"
                />
                <Button variant="outline-secondary" className="rounded-0" onClick={addToCart}>
                    +
                </Button>
            </div>
            <Button className="ml-2" variant="primary">
                Agregar al Carrito
            </Button>
        </div>
    );
};



export default CartButtons;
